class FinReport < ActiveRecord::Base
  attr_accessible :fd_code, :fd_profit_after_share, :fd_profit_after_tax, :fd_profit_base_share, :fd_repdate, :fd_turnover, :fd_type, :fd_year

  TYPE_Q1 = 4
  TYPE_Q2 = 3
  TYPE_Q3 = 2
  TYPE_ANNUAL = 1

  def self.import_finRpt
    stocks = Stock.all
    stocks.each do |s|
      records = []
      if s.stamp == "us"
        records = import_us_finRpt s
      else
        records = import_hk_finRpt s
      end

      FinReport.transaction do
        records.each do |item|
          begin
          FinReport.create item
          rescue
          end
        end
      end
    end
  end

