  def self.import_us_finRpt stock
    regexp = /([A-Z]+)/
    code = stock.code.match(regexp)[1]
    url = "http://stockpage.10jqka.com.cn/#{code}/finance/"
    rsp = Net::HTTP.get(URI.parse(url))
    regexp = /<p id="benefit">(.*)<\/p>/
    match_data = rsp.match(regexp)
    unless match_data
      return []
    end

    json_str = match_data[1]
    parsed_json = ActiveSupport::JSON.decode(json_str)

    report = parsed_json["report"]

    records = []
    cols = []
    # fd_year, fd_type
    # "2016-06-30<br>2016\u5e74\u4e2d\u62a5"
    report[0].each_with_index  do |cell,idx|
      unless cell.to_s.match(/^20[012][0-9]/)
        next
      end

      fd_repdate, fd_year = cell.split("<br>")
      if fd_year.end_with? "年报"
        fd_year = fd_year.match(/(\d+)/)[1]
        fd_type = FinReport::TYPE_ANNUAL
        cols << idx
        records << {fd_code:stock.code, fd_year:fd_year, fd_repdate:fd_repdate, fd_type:fd_type}
      end
    end

    # fd_turnover
    cols.each_with_index do |col,idx|
      records[idx]["fd_turnover"] = report[1][col]
      records[idx]["fd_profit_base_share"] = report[-2][col]
      records[idx]["fd_profit_after_share"] = report[-1][col]
    end

    records
  end

