  def self.import_hk_finRpt stock
    url = "http://web.ifzq.gtimg.cn/appstock/hk/HkInfo/getFinReport?type=3&reporttime_type=1&code=#{stock.code}&&startyear=2010&endyear=#{Time.now.year}"
    rsp = Net::HTTP.get(URI.parse(url))
    json_str = rsp
    parsed_json = ActiveSupport::JSON.decode(json_str)
    items = parsed_json["data"]["data"]

    records = items.map do |item|
      {fd_code:stock.code, fd_year:item["fd_year"], fd_repdate:itm["fd_repdate"], fd_type:item["fd_type"],
       fd_turnover:item["fd_turnover"],fd_profit_base_share:item["fd_profit_base_share"],fd_profit_after_share:item["fd_profit_after_share"]}
    end

    records
  end
